glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
  #if USE_TEXTURE
    in vec2 v_uv0;
    uniform sampler2D texture;
  #endif
uniform FragConstants {
  float time;
};
void main () {
  vec4 src_color = texture2D(texture, v_uv0);
  float light_width = 0.2;
  float speed = 2.0;
  float start_x = speed * time;
  float light_strength = 0.006;
  float offset = 5.0;
  float y1 = -1.0 * offset * (v_uv0.x - start_x);
  float y2 = -1.0 * offset * (v_uv0.x - start_x - light_width);
  if (v_uv0.y > y1 && v_uv0.y < y2) {
    float dis = v_uv0.y * -1.0 / offset + (light_width / 2.0) + start_x - v_uv0.x;
    if (dis < 0.0) {
      dis = dis * -1.0;
    }
    float sc = dis / (light_width / 2.0);
    float lightA = (1.0 - sin(sc * 3.1415926536 / 2.0));
    lightA *= 0.7;
    if (lightA < 0.0) lightA = 0.0;
    else if (lightA > 0.6) lightA = 0.6;
    lightA *= light_strength;
    float newr = src_color.r + 255.0 * lightA;
    float newg = src_color.g + 255.0 * lightA;
    float newb = src_color.b + 255.0 * lightA;
    float newa = src_color.a + 1.0 * lightA;
    gl_FragColor = vec4(newr, newg, newb, newa);
  } else {
      gl_FragColor = src_color;
  }
}

